import { Component, Input, OnChanges, OnInit } from '@angular/core';

@Component({
  templateUrl: './testing.component.html',
  selector: 'test-component'
})


export class TestingComponent implements OnInit {

  show = false;
  MyText: string = "My Test String";

  @Input('testInput') MyTestInput: string = "";

  toggleShow() {
    console.log(this.MyTestInput);
    this.show = !this.show;
    this.MyText = this.MyTestInput;
  }

  ngOnInit() {
    console.log("Init fired");
  }

}
<div>

  <h1>{{MyText}}</h1>
  
  <div>
    <button mat-raised-button id="MyButton" (click)="toggleShow()">Test</button>
    <input ng-bind="testInput" />
  </div>

</div>
